#!/usr/bin/env python3

dna = ''
dna_upper=dna.upper()
dna_rev=dna_upper[::-1]
dna_revcomp1=dna_rev.replace("A","T")
dna_revcomp2=dna_revcomp1.replace("T","A")
dna_revcomp3=dna_revcomp2.replace("C","G")
reversecomplement=dna_revcomp3.replace("G","C")

print(f"{'Original Sequence':<20} 5\' {dna} 3\'")
print(f"{'Complement':<20} 3\' {dna_rev} 3\'")
print(f"{'Reverse Complement':<20} 5\' {reversecomplement} 3\'")
