#!/usr/bin/env python3

dna = ''
uniquents = set('')
counts = {}
#initialize counts dictionary
for base in uniquents:
    if base in counts:
        continue
    else:
        counts[base]=0
#print(counts)

#add to each counts going through original dna per unique base
for nt in uniquents:
    for nt2 in dna:
        if nt2 == nt:
            counts[nt]+=1


print(f'Nucleotide Composition:')
print(f'A: {counts['A']}')
print(f'T: {counts['T']}')
print(f'G: {counts['G']}')
print(f'C: {counts['C']}')
#calculate GC content:
gc=(counts['G']+counts['C'])/(counts['T']+counts['A']+counts['G']+counts['C'])
#check if total counts = len(dna)
#if (counts['T']+counts['A']+counts['G']+counts['C']) == len(dna):
    #print('true')
    #yest this works
gcpercent=gc*100
print(f'GC content: {gcpercent}%')
